
public class MathMethods {

   public static void main(String[] args) {
      // TODO Auto-generated method stub
      System.out.println("\n\nMaths are automatically done as double types");
      System.out.println("\nThis means that 2^5 is " +Math.pow( 2, 5 ));
      System.out.println("\nButttttt, I want integers! WAHHHH!!!!");
      System.out.println("\nOkay, whiney.... " +(int)Math.pow( 2, 5 ));
   }
   

}
